package module2;

public class Pattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1;i<=5;i++)
		{
			for(int j=1;j<=i;j++)
			{
				//System.out.print("1"+" ");
			if(i==j)
				System.out.print("1"+" ");
			else
				System.out.print(" ");
			}
			System.out.print("\n");
			
		}
}
}
